//*****************************************************************************************************//
//************************************* LUT Target Description ****************************************//
//*****************************************************************************************************//
struct cm_tables {
    unsigned int con;
    // GF0/GF1/VG/VGS/VGF/VGRFS
    unsigned int *eotf;       // SZ_CM_EOTF       96
    unsigned int *gm;         // SZ_CM_GM         5
    unsigned int *oetf;       // SZ_CM_OETF       33
    unsigned int *sc;         // SZ_CM_SC         1
    // VGRFS
    // priority 0 : DTM
    unsigned int *tm_dynamic; // SZ_CM_TM_DYNAMIC 40
    // priority 1 : STM
    unsigned int *tm_ext;     // SZ_CM_TM_EXT     7
    // VG/VGS/VGF/VGRFS only
    unsigned int *tm_tune;    // SZ_CM_TM_TUNE    1
    unsigned int *tm_gamut;   // SZ_CM_TM_GAMUT   2
    unsigned int *tm_curve;   // SZ_CM_TM_CURVE   30
    // priority 2 : SDR
    unsigned int *tm_off;     // SZ_CM_TM_OFF     1
};

enum cm_table_size {
    SZ_CON        = 1,
    SZ_GM         = 5,
    SZ_EOTF       = 96,
    SZ_OETF       = 33,
    SZ_SC         = 1,
    // static
    SZ_TM_TUNE    = 1,
    SZ_TM_GAMUT   = 2,
    SZ_TM_CURVE   = 30,
    SZ_TM_EXT     = 7,
    // dynamic
    SZ_TM_DYNAMIC = 40,
    // exception handling
    SZ_TM_OFF     = 1,
};

